import React, { useEffect } from "react";
import ScreenHeading from "../../utilities/ScreenHeading/ScreenHeading";
import ScrollService from "../../utilities/ScrollService";
import Animations from "../../utilities/Animations";
import "./AboutMe.css";

function AboutMe(props) {
    let fadeInScreenHandler = (screen) => {
        if (screen.fadeInScreen !== props.id) return;
        Animations.animations.fadeInScreen(props.id);
    };
    const fadeInSubscription = ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);

    const SCREEN_CONSTANTS = {
        description: "Full stack web developer with a knack of building applications with utmost efficiency.",
        highlights: {
            bullets: ["Full stack web development", "React and React Native", "Building REST API", "Managing database"],
            heading: "Here are a few highlights:",
        },
    };
    return (
        <div className="about-me-container screen-container">
            <div className="about-me-parent">
                <ScreenHeading title={"About Me"} subHeading={"Why Choose Me?"} />
            </div>
        </div>
    );
}

export default AboutMe;
